import test from './/assets/test.jpg'
//const apiKey = process.env.STRIPE_KEY;
require('dotenv').config();
const keyOne = process.env.KEY_ONE;
const keyTwo = process.env.KEY_TWO;
const keyThree = process.env.KEY_THREE;


const PRODUCTS = [
    {
        id: keyOne,
        title: "a",
        price: 1.00,
        productImage: test,
    },
    {
        id: keyTwo,
        title: "b",
        price: 2.00,
        productImage: test,
    },
    {
        id: keyThree,
        title: "c",
        price: 3.00,
        productImage: test,
    }
];

function getProductData(id) {
    let productData = PRODUCTS.find(product => product.id === id);

    if (productData === undefined) {
        console.log("Product data does not exist for ID:" + id);
        //just a check :)
        return undefined;
    }

    return productData;
}

export { PRODUCTS, getProductData };
