import '../styles/styles.css';
import { Card, Button, Form, Row, Col } from 'react-bootstrap';
import { cartcontext } from '../cartcontext';
import { useContext } from 'react';

const images = {
    'yellow-cow': require('./assets/yellow-cow.jpg').default,
    'love-cow': require('./assets/love-cow.jpg').default,
    'spiderman': require('./assets/spiderman.jpg').default,
  };

function ProductCard(props) { // props.product is the product we are selling
    const product = props.product;
    const cart = useContext(cartcontext);
    const productQuantity = cart.getProductQuantity(product.id);
    const productImage = images[product.image];

    console.log(cart.items);
    return (
        <Card>
            <Card.Body>
                <Card.Title>{product.title}</Card.Title>
                <Card.Text>${product.price}</Card.Text>
                <Card.Img variant="top" src={product.image} alt={product.title} className="card-img" />
                <hr/>
                { productQuantity > 0 ?
                    <>
                        <Form as={Row}>
                            <Form.Label column="true" sm="6">In Cart: {productQuantity}</Form.Label>
                            <Col sm="6">
                                <Button sm="6" onClick={() => cart.addOneToCart(product.id)} className="mx-2">+</Button>
                                <Button sm="6" onClick={() => cart.removeOneFromCart(product.id)} className="mx-2">-</Button>
                            </Col>
                        </Form>
                        <Button variant="danger" onClick={() => cart.deleteFromCart(product.id)} className="my-2">Remove from cart</Button>
                    </>
                    :
                    <Button variant="primary" onClick={() => cart.addOneToCart(product.id)}>Add To Cart</Button>
                }
            </Card.Body>
        </Card>

    )
}

export default ProductCard;
